package Day2Oct3Assignments;

public class CheckIfTheNumberIsPositiveOrNegative {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=1;
		 
		 if (number >0) {
			 System.out.println("The number is +ve");
		 }
		 else if (number < 0) {
			 System.out.println("The number is -ve");
		 }
			 else
			 { System.out.println("The number is neither +ve nor -ve");
			 }
	}

}
